import { useEffect, useState } from "react";
import { Layout } from "../layout/layout";

export function Home() {
  const [data, setData] = useState([]);
  useEffect(() => {
    async function getData() {
      const res = await fetch(
        "https://v2.api.noroff.dev/auction/listings?limit=20"
      );
      const data = await res.json();
      console.log(data);
      setData(data.data);
    }
    getData();
  }, []);

  return (
    <>
      <Layout>
        <h1 className="text-3xl font-bold">ALL LISTINGS</h1>
        <div className="max-w-7xl flex gap-4 flex-wrap m-auto">
          {data.map((listing, i) => {
            return (
              <a key={i} href={"/" + listing.id}>
                <div className="w-sm h-60 flex flex-col p-4 shadow-2xl">
                  <h2>{listing.title}</h2>
                  {listing.media.length ? (
                    <img
                      src={listing.media[0].url}
                      className="max-h-full max-w-full object-cover"
                    />
                  ) : (
                    <img src="data:image/jpeg;base64," />
                  )}
                  <p>{listing.description}</p>
                </div>
              </a>
            );
          })}
        </div>
      </Layout>
    </>
  );
}
